# [409] 最长回文串
#

# @lc code=start
class Solution:
    def longestPalindrome(self, s: str) -> int:
        max_len = 0
        res = {}
        for item in s:
            res[item] = res.get(item, 0) + 1
        print(res)
        if len(res) == 1:
            return len(s)
        for item in res:
            temp = res.get(item)
            if temp % 2 == 0:
                max_len += temp
            else:
                if max_len % 2 == 0:
                    max_len += temp
                else:
                    max_len += temp -1
        return max_len
# @lc code=end

s = ""
so  = Solution()
print(so.longestPalindrome(s))